class ReaderFallbackCatalog {
  static const Map<String, int> _coreLanguageTargets = {
    'grc-koi': 10,
    'grc-cls': 10,
    'lat': 10,
    'hbo': 10,
  };

  static String _normalizeLanguage(String language) {
    // Keep specific language codes separate - don't normalize them
    return language;
  }

  const ReaderFallbackCatalog._();

  static bool hasLanguage(String language) =>
      _fallbackCatalog.containsKey(language);

  static List<TextWorkInfo> worksForLanguage(String language) {
    final normalized = _normalizeLanguage(language);
    final works = _fallbackCatalog[normalized];
    final requiredCount = _coreLanguageTargets[normalized];
    assert(
      requiredCount == null || (works?.length ?? 0) >= requiredCount,
      'ReaderFallbackCatalog requires at least $requiredCount curated works for "$normalized"; '
      'found ${works?.length ?? 0}. Update reader_fallback_catalog.dart before shipping.',
    );
    if (works == null) return const [];
    return works
        .map(
          (work) => TextWorkInfo(
            id: work.id,
            author: work.author,
            title: work.title,
            language: work.language,
            refScheme: 'section',
            segmentCount: work.segments.length,
            licenseName: 'Curated Public Domain',
            sourceTitle: 'Reader Fallback Catalog',
            preview: work.segments.isNotEmpty ? work.segments.first : null,
          ),
        )
        .toList(growable: false);
  }

  static _FallbackWork? _resolve(int textId) {
    for (final entry in _fallbackCatalog.values) {
      for (final work in entry) {
        if (work.id == textId) return work;
      }
    }
    return null;
  }

  static TextStructureResponse? structureFor(int textId) {
    final work = _resolve(textId);
    if (work == null) return null;
    final chapters = <Map<String, dynamic>>[];
    for (var index = 0; index < work.segments.length; index++) {
      final section = index + 1;
      final ref = 'S$section.1';
      chapters.add({
        'label': 'Section $section',
        'ref_start': ref,
        'ref_end': ref,
      });
    }
    return TextStructureResponse(
      structure: TextStructure(
        textId: work.id,
        title: work.title,
        author: work.author,
        refScheme: 'section',
        chapters: chapters,
      ),
    );
  }

  static TextSegmentsResponse? segmentsFor(
    int textId,
    String refStart,
    String refEnd,
  ) {
    final work = _resolve(textId);
    if (work == null) return null;
    int parseSection(String ref) {
      final parts = ref.split('.');
      if (parts.isEmpty) return 0;
      final numeric = parts.first.replaceAll(RegExp('[^0-9]'), '');
      return int.tryParse(numeric) ?? 0;
    }

    final start = parseSection(refStart);
    final end = parseSection(refEnd);
    final segments = <SegmentWithMeta>[];
    for (var index = 0; index < work.segments.length; index++) {
      final section = index + 1;
      if (start != 0 && section < start) continue;
      if (end != 0 && section > end) break;
      final ref = 'S$section.1';
      segments.add(
        SegmentWithMeta(
          ref: ref,
          text: work.segments[index],
          meta: {'section': section},
        ),
      );
    }
    return TextSegmentsResponse(
      segments: segments,
      textInfo: {
        'author': work.author,
        'title': work.title,
        'source': 'Reader Fallback Catalog',
        'license': 'Curated Public Domain',
      },
    );
  }

  static String? previewFor(int textId) {
    final work = _resolve(textId);
    if (work == null || work.segments.isEmpty) {
      return null;
    }
    return work.segments.first;
  }
}
